<script>
    // VARIABEL MAX SLIDER
    const DEFAULT_MAX = 100;

    // INPUT MANUAL ON
    document.getElementById('create-distance-display').addEventListener('click', function() {
        this.style.display = 'none';
        const manualInput = document.getElementById('create-distance-manual');
        manualInput.style.display = 'inline';
        manualInput.focus();
    });

    // INPUT MANUAL AUTO-OFF
    document.getElementById('create-distance-manual').addEventListener('blur', updateDistanceFromManual);

    // UPDATE BY ENTER
    document.getElementById('create-distance-manual').addEventListener('keypress', function(e) {
        if (e.key === 'Enter') {
            updateDistanceFromManual();
            e.preventDefault();
        }
    });

    // FUNGSI UPDATE MANUAL
    function updateDistanceFromManual() {
        const manualInput = document.getElementById('create-distance-manual');
        let newValue = parseFloat(manualInput.value);
        if (isNaN(newValue)) {
            newValue = 0;
        }

        // UPDATE DISPLAY TEXT
        const displaySpan = document.getElementById('create-distance-display');
        displaySpan.textContent = newValue;

        // UPDATE SLIDER
        const slider = document.getElementById('create-distance');
        if (newValue > DEFAULT_MAX) {
            slider.setAttribute('max', newValue);
        } else {
            slider.setAttribute('max', DEFAULT_MAX);
        }

        slider.value = newValue;
        updateCreateDistanceFromManual(newValue);

        // HIDE MANUAL INPUT
        manualInput.style.display = 'none';
        displaySpan.style.display = 'inline';
    }

    // CALL FORM
    function selectCreateTransport(element) {
        // AUTO REMOVE ACTIVE OPTIONS
        document.querySelectorAll('#createCalcForm .transport-option').forEach(el => el.classList.remove('active'));
        element.classList.add('active');

        const selectedTransport = element.getAttribute('data-transport');
        const fuelRequired = element.getAttribute('data-fuel-required') === 'true';
        document.getElementById('create-transport-input').value = selectedTransport;

        const fuelOptions = document.getElementById('create-fuel-options');
        if (fuelRequired) {
            fuelOptions.classList.remove('hidden');
        } else {
            fuelOptions.classList.add('hidden');
            document.querySelectorAll('#createCalcForm .fuel-option').forEach(el => el.classList.remove('active'));
            document.getElementById('create-fuel-type-input').value = '';
        }
        recalcCreateEmissions();
    }

    // SHOW FUEL OPTIONS
    function selectCreateFuel(element) {
        document.querySelectorAll('#createCalcForm .fuel-option').forEach(el => el.classList.remove('active'));
        element.classList.add('active');
        const selectedFuel = element.getAttribute('data-fuel');
        document.getElementById('create-fuel-type-input').value = selectedFuel;
        recalcCreateEmissions();
    }

    // UPDATE SLIDER FROM MANUAL
    function updateCreateDistanceFromManual(value) {
        const slider = document.getElementById('create-distance');
        if (parseFloat(slider.value) < DEFAULT_MAX) {
            slider.setAttribute('max', DEFAULT_MAX);
        }
        document.getElementById('create-distance-display').textContent = value;
        recalcCreateEmissions();
    }

    // UPDATE DISTANCE
    function updateCreateDistance(value) {
        const slider = document.getElementById('create-distance');
        if (parseFloat(slider.value) > DEFAULT_MAX) {
            slider.setAttribute('max', DEFAULT_MAX);
        }
        document.getElementById('create-distance-display').textContent = value;
        recalcCreateEmissions();
    }

    // UPDATE KALKULASI
    function recalcCreateEmissions() {
        const transport = document.getElementById('create-transport-input').value;
        const fuelType = document.getElementById('create-fuel-type-input').value;
        const distance = parseFloat(document.getElementById('create-distance').value) || 0;
        let emissionFactor = 0;

        switch (transport) {
            case 'mobil':
                if (fuelType === 'bensin') {
                    emissionFactor = 0.21;
                } else if (fuelType === 'solar') {
                    emissionFactor = 0.19;
                } else if (fuelType === 'listrik') {
                    emissionFactor = 0.05;
                } else {
                    emissionFactor = 0;
                }
                break;
            case 'motor':
                if (fuelType === 'bensin') {
                    emissionFactor = 0.13;
                } else if (fuelType === 'solar') {
                    emissionFactor = 0.11;
                } else if (fuelType === 'listrik') {
                    emissionFactor = 0.02;
                } else {
                    emissionFactor = 0;
                }
                break;
            case 'sepeda':
                emissionFactor = 0.00;
                break;
            case 'bus':
                emissionFactor = 0.08;
                break;
            case 'kereta':
                emissionFactor = 0.06;
                break;
            case 'pesawat':
                emissionFactor = 0.24;
                break;
            default:
                emissionFactor = 0;
        }

        const totalCO2 = distance * emissionFactor;
        const trees = totalCO2 / 21.7;
        document.getElementById('create-emissions-display').textContent = totalCO2.toFixed(2) + ' kg';
        document.getElementById('create-trees-display').textContent = trees.toFixed(2);
    }

    document.addEventListener('DOMContentLoaded', function() {
        updateCreateDistance(document.getElementById('create-distance').value);
    });
</script>
